import React, { useState } from "react";
import "../css/ViewContact.css";
import { useAppContext } from "../appContext";
import { useNavigate } from "react-router-dom";


const ViewContact = () => {
  const {contacts} = useAppContext();
  const navigate = useNavigate();
  const onClick = () => {
    navigate("/editContact/1");
  };
  return (
    <div >
        <table>
           <thead>
           <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Age</th>
                <th>City</th>
                <th>Edit</th>

            </tr>
           </thead>
            <tbody>
            {
                contacts.map((contact,index)=>(
                    <tr key={index}>
                        <td>{index+1}</td>
                        <td>{contact.name}</td>
                        <td>{contact.age}</td>
                        <td>{contact.city}</td>
                        <td onClick={onClick}>edit</td>
                    </tr>
                ))
            }
            </tbody>
        </table>
    </div>
  )
};

export default ViewContact;
