import { SAVE_CONTACT } from "./action";

export const appReducer = (state, action) => {
  switch (action.type) {
    case SAVE_CONTACT:
      return {
        ...state,
        contacts: [...state.contacts, action.payload.contact]
      };

    default:
      return state;
  }
};
