import React, { useEffect, useState } from "react";
import { editContact, saveContact } from "../action";
import { useAppContext } from "../appContext";
import { useNavigate, useParams } from "react-router-dom";

const AddContact = () => {
  const { dispatch, contacts } = useAppContext();
  const [isEdit, setIsEdit] = useState(false);
  const [isFormValid, setIsFormValid] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();
  const onSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    if (isEdit) {
      contacts[id] = {
        name: data.get("name"),
        age: data.get("age"),
        city: data.get("city"),
      };
      editContact(dispatch, contacts);
    } else {
      saveContact(dispatch, {
        name: data.get("name"),
        age: data.get("age"),
        city: data.get("city"),
      });
    }
    navigate("/");
  };
  const getContactByIndex = (index) => {
    const contact = contacts[index];
    if (contact) {
      setIsEdit(true);
    } else {
      alert("Please enter a valid id");
      navigate("/");
    }
  };

  useEffect(() => {
    if (id) {
      getContactByIndex(id);
    } else {
      setIsEdit(false);
    }
  }, [id]);

  const formValidation = (event) => {
    if (event.target.value == "name"){
      if (event.target?.value == null|| event.target?.value == "" || event.target?.value == "0-9" || event.target?.length <3) {
        alert("Enter a valid Name");
        setIsFormValid(false);
      } else {
        setIsFormValid(true);
      }
    }
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label>Name:</label>
        <br />
        <input
          type="text"
          id="name"
          name="name"
          defaultValue={isEdit ? contacts[id]?.name : ""}
          placeholder="Your Name"
          onClick={formValidation}
          required
        />
        <br />
        <label>Age:</label>
        <br />
        <input
          type="number"
          id="age"
          name="age"
          defaultValue={isEdit ? contacts[id]?.age : ""}
          placeholder="Your Age"
          required
        />
        <br />
        <label>City:</label>
        <br />
        <input
          type="text"
          id="city"
          name="city"
          defaultValue={isEdit ? contacts[id]?.city : ""}
          placeholder="Your Place"
          required
        />
        <br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default AddContact;
