import React, { useEffect, useState } from "react";
import { saveContact } from "../action";
import { useAppContext } from "../appContext";
import { useNavigate, useParams } from "react-router-dom";

const AddContact = () => {
  const { dispatch, contacts } = useAppContext();
  const [isEdit, setIsEdit] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();
  const onSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    saveContact(dispatch, {
      name: data.get("name"),
      age: data.get("age"),
      city: data.get("city"),
    });
    navigate("/");
  };
  const getContactByIndex = (index) => {
    const contact = contacts[index];
    if (contact) {
      setIsEdit(true);
    } else {
      alert("Please enter a valid id");
      navigate("/");
    }
  };
  useEffect(() => {
    if (id) {
      getContactByIndex(id);
    } else {
      setIsEdit(false);
    }
  }, [id]);

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label>Name:</label>
        <br />
        <input
          type="text"
          id="name"
          name="name"
          defaultValue={isEdit ? contacts[id].name : ""}
          required
        />
        <br />
        <label>Age:</label>
        <br />
        <input type="number" id="age" name="age" defaultValue={isEdit ? contacts[id].age : placeholder = "Age"} required />
        <br />
        <label>City:</label>
        <br />
        <input type="text" id="city" name="city" required />
        <br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default AddContact;
